module.exports = memoryStore;

function memoryStore () {
    var store = {};

    return {
        name: 'memory',
        set: set,
        get: get,
        remove: remove
    };

    function set (k, v) {
        store[k] = v;
    }

    function get (k) {
        return store[k];
    }

    function remove (k) {
        delete store[k];
    }
}
