"use strict";

/* eslint-disable no-magic-numbers */

// parse the exp cookie

module.exports = function(xpm, xpa) {
  if (!xpm || !xpa) return undefined;

  //
  // exp cookie spec at
  // https://confluence.walmart.com/display/PGPABTEST/Expo+Cookie+Optimization+-+Design
  //

  // assignment cookie format: var1|var2|var3
  // variant IDs are guaranteed to be unique so passing only that to CCM is enough
  const varIds = xpa.split("|");

  return { varIds };
};
