"use strict";

function convertValue(value) {
  value = value.trim();
  // string enclosed by quotes
  const m = value.match(/(^['"`])/);
  if (m && value.endsWith(m[1])) {
    return value.substring(1, value.length - 1);
  }
  // true or false
  if (value === "true") return true;
  if (value === "false") return false;
  if (value.match(/^[+\-\.0-9]*$/)) {
    const n = parseFloat(value);
    if (!isNaN(n)) return n;
  }
  // treat as string
  return value;
}

module.exports = convertValue;
