"use strict";

const config = require("../..");

describe.only("config", function () {
  it("should allow calling initialize first", () => {
    const result = {
      "json": "json",
      "oneops": "",
      "deployment": "development"
    };

    delete process.env.ONEOPS_ENVPROFILE;
    process.env.NODE_CONFIG_DIR = "test/config";
    config.initialize();

    expect(config.config).to.deep.equal(result);
  });
});

