"use strict";

const electrodeFetch = require("./server-fetch");
const pkg = require("../package.json");
const { isHapi17 } = require("./util");

function register16(server, options, next) {
  electrodeFetch.setServer(server);
  next();
}
function register17(server) {
  electrodeFetch.setServer(server);
}
if (!isHapi17) {
  register16.attributes = { pkg };
  module.exports = register16;
} else {
  module.exports = {
    register: register17,
    pkg
  };
}
