"use strict";

module.exports = opt => {
  let x = "true";

  if (process.env.ELECTRODE_FYI_COLOR !== undefined) {
    x = process.env.ELECTRODE_FYI_COLOR;
  } else if (typeof opt === "string") {
    x = opt;
  } else if (typeof opt === "boolean") {
    x = `${opt}`;
  }

  x = x.toUpperCase();

  return x === "TRUE" || x === "YES";
};
