"use strict";

module.exports = function(context) {
  const user = context.user;

  const jsBundle = user.routeData.WEBPACK_DEV ? user.devJSBundle : user.jsBundle;

  if (!jsBundle || user.renderJs === false) return "";

  return `<script src="${jsBundle}"${user.routeData.bodyBundleProps}></script>`;
};
