"use strict";

const resolveDeviceType = require("@walmart/psych/lib/resolve-device-type");

module.exports = context => {
  const isMobile = resolveDeviceType(context.user.request.headers) === "mobile";
  if (!isMobile) return "";
  // eslint-disable-next-line
  return `<meta key="apple-mobile-web-app-capable" name="apple-mobile-web-app-capable" content="yes"/>`;
};
