"use strict";

/* eslint-disable max-depth */

const { getContent } = require("electrode-react-webapp/lib/react/content");
const Promise = require("bluebird");

module.exports = context => {
  return Promise.try(() => getContent(context.user.renderSs, context.options, context)).then(
    userContent => {
      context.user.content = userContent;
    }
  );
};
