"use strict";

const objectToProps = require("../utils/object-to-props");

const inject = grouped => {
  return grouped.scripts
    .map(scr => {
      if (typeof scr === "string") {
        return `<script>${scr}</script>`;
      } else {
        return scr.map(scr2 => `<script${objectToProps(scr2)}></script>`).join("");
      }
    })
    .join("");
};

module.exports = inject;
