"use strict";

/*
 * This file contains the function that's meant to be sent to the browser
 * for translating assets such as images and styles by using their webpack
 * filename to lookup the URL to the copy uploaded to the CDN server.
 *
 * It expects the CDN URL mapping to be in window._wml.cdn.md.
 */

module.exports = function (f) {
  var x = window._wml && window._wml.cdn;
  if (x && x.md) {
    for (var k in x.md) {
      if (k.indexOf(f) >= 0) {
        return x.md[k];
      }
    }
  }
};