"use strict";

const symName = name => `electrode-log-transaction.symbols.${name}`;

module.exports = {
  mainThread: Symbol.for(symName("mainThread")), // for main txn thread on request
  referredContext: Symbol.for(symName("referredContext")), // for referred context on request

  // Note: electrode-logging v3 independently look for context in request
  // with a symbol lookup from the same name, which is
  // electrode-log-transaction.symbols.context
  context: Symbol.for(symName("context")), // for passing context in data to logging

  leafMarker: "@leaf",
  contextData: Symbol(symName("context.data")) // for internal context data
};
