"use strict";

const constants = require("./constants");

const injectContext = (context, data) => {
  if (typeof data === "string") {
    data = { msg: data, [constants.context]: context };
  } else if (!data) {
    data = { [constants.context]: context };
  } else if (!data[constants.context]) {
    data[constants.context] = context;
  }

  return data;
};

module.exports = injectContext;
