"use strict";

const insertProcessInfo = function(path) {
  const logSuffix = ".log";
  let prefix = path;
  let postfix = "";

  if (path.endsWith(logSuffix)) {
    prefix = prefix.substr(0, path.length - logSuffix.length);
    postfix = logSuffix;
  }

  return `${prefix}-${process.env.NODE_APP_INSTANCE}${postfix}`;
};

module.exports = {
  insertProcessInfo
};
