"use strict";

module.exports = function(environment) {
  const developmentMap = {
    apm: "stdout",
    file: "file",
    logmon: "stdout",
    splunk: "stdout",
    stdout: "stdout"
  };

  const productionMap = {
    apm: "apm",
    file: "file",
    logmon: "logmon",
    splunk: "splunk",
    stdout: "stdout"
  };

  return environment === "development" ? developmentMap : productionMap;
};
