"use strict";

const base = require("./base");

const sendMetric = (type, label, value) => {
  const data = {
    metricType: type,
    value
  };

  base.sendEvent(label, data);
};

module.exports = {
  histogram: (label, value) => sendMetric("histogram", label, value),
  meter: (label, value) => sendMetric("meter", label, value)
};
