"use strict";

const _ = require("lodash");

const logWithSuppression = servicesConfig => {
  const autoInitBeQuiet = servicesConfig.autoInitBeQuiet === true;
  const autoInitNoCautionWarnings = servicesConfig.autoInitNoCautionWarnings === true;

  return {
    log: autoInitBeQuiet ? _.noop : console.log, //eslint-disable-line no-console
    error: autoInitNoCautionWarnings ? _.noop : console.error //eslint-disable-line no-console
  };
};

module.exports = logWithSuppression;
