/* eslint-disable strict, prefer-template */
"use strict";

const ElectrodeSwapi = require("../../lib/electrode-swapi.js");
const Path = require("path");

class OOClient extends ElectrodeSwapi {
  constructor(config, swagger, providerName) {
    super(providerName || OOClient.providerName, config, swagger);
  }

  static create(config, specOverride, providerName) {
    return ElectrodeSwapi.create({ Client: OOClient, config, specOverride, providerName });
  }

  static get specFilename() {
    return Path.join(__dirname, "../data/swagger.yaml");
  }

  static get providerName() {
    return "oneOps";
  }

  static get serviceName() {
    return "one-ops-service";
  }

  static get serviceVersion() {
    return "1.0.0";
  }

  generateOperations() {
    if (this._Operations) {
      return;
    }
    super.generateOperations();
    this._Operations.prototype.testSwag = () => "testSwag";
  }
}

module.exports = OOClient;
