/* eslint-disable strict, prefer-template */
"use strict";

const ElectrodeSwapi = require("../../lib/electrode-swapi.js");
const Path = require("path");

class TestClient extends ElectrodeSwapi {
  constructor(config, swagger, providerName) {
    super(providerName || TestClient.providerName, config, swagger);
  }

  static create(config, specOverride, providerName) {
    return ElectrodeSwapi.create({ Client: TestClient, config, specOverride, providerName });
  }

  static get specFilename() {
    return Path.join(__dirname, "./test-server.yaml");
  }

  static get providerName() {
    return "test";
  }

  static get serviceName() {
    return "test-service";
  }

  static get serviceVersion() {
    return "1.0.0";
  }
}

module.exports = TestClient;
