"use strict";

const join = paths => {
  const x = paths.filter(p => p).join("/");

  if (paths[0] === "/" && x === "/") {
    // returning "" for [ "/", "", "", ... ]
    return "";
  }

  return x.match(/^\/+$/) // avoid returning "" for [ "/", "/", ... ]
    ? "/"
    : x.replace(/\/+/g, "/").replace(/\/$/, ""); // remove multiple /'s and trailing /
};

module.exports = function(config) {
  config = config || {};
  const ui = config.ui || {};
  const basePath = ui.basePath || "";
  return {
    ui,
    ccm: config.ccm || {},
    getCcm: () => config.ccm || {},
    expoCookies: config.expoCookies || {},
    fullPath: path => join([basePath, path]),
    fullApiPath: path => join([basePath, ui.apiPath || "/api", path])
  };
};
