var TORBIT_REQUEST_HEADER_NAME = "x-tb-browser";
var torbitValueToBrowserType = {
  "b": "browser",
  "a": "application",
  "t": "tenantNativeApplication",
  "c": "crawler",
  "u": "unknown"
}

module.exports = function (headers) {
  if (!headers || !headers[TORBIT_REQUEST_HEADER_NAME]) {
    return "unknown";
  }

  var browserType = torbitValueToBrowserType[headers[TORBIT_REQUEST_HEADER_NAME]];

  return browserType ? browserType : "unknown";
};
