// Load modules
var Lab = require("lab");
var Code = require("Code");
var lab = exports.lab = Lab.script();

var describe = lab.describe;
var it = lab.it;
var before = lab.before;
var after = lab.after;
var expect = Code.expect;

var Psych = require("..");

var TORBIT_HEADER_NAME = "x-tb-browser";

describe("is-crawler", function () {
  it("should be a function", function (done) {
    expect(Psych.isCrawler).to.be.a.function();
    done();
  });

  it("should return false if browser type is not crawler", function (done) {
  	var browserType = Psych.isCrawler({});

   	expect(browserType).to.equal(false);
    done();
  });

  it("should return true if browser type is crawler", function (done) {
    var headers = {};
    headers[TORBIT_HEADER_NAME] = "c";
    var browserType = Psych.isCrawler(headers);

    expect(browserType).to.equal(true);
    done();
  });
});
